import sys,osimport numpy as npimport cv2path = "D:\Projects\Thesis\Datasets\Kaggle\compressedTrain"outpath = "D:\Projects\Thesis\Datasets\Kaggle\cropImages/"for file in os.listdir(path):    inp = path+'/'+file    out = outpath + '/' + file    img = cv2.imread(inp)    print(file)    # print(img.shape)    xmin=img.shape[0]    xmax=0    ymin=img.shape[1]    ymax=0    for i in range(img.shape[0]):        for j in range(img.shape[1]):            if np.all(img[i][j]>30):                if xmin>i:                    xmin=i                    # print(img[i][j])                if xmax<i:                    xmax=i                    # print(img[i][j])                if ymax<j:                    ymax=j                    # print(img[i][j])                if ymin>j:                    ymin = j                    # print(img[i][j])    # print(xmax,xmin, ymax, ymin)    if xmin<xmax and ymin<ymax:        crop_img = img[xmin:xmax, ymin:ymax]        cv2.imwrite(out, crop_img)    # cv2.imshow("cropped", crop_img)    # cv2.waitKey(0)    # sys.exit(0)    # for i in range(len(img)):